frappe.ui.form.on("Payment Entry", "validate", function(frm) {
    let references = [];
    $.each(frm.doc.references, function(idx, row) {
        in_list(references, row.reference_name) ? "" : references.push(row.reference_name)    });
    if (frm.doc.__islocal && !frm.doc.reference_no) {
        var ref;
        var refid;
        ref = references.toString().replace(/-/g, "").substr(2);
        refid = references.toString().substr(0, 2);
        if (refid == "RA") {
            frm.set_value("reference_no", "SI00" + ref);
        } else frm.set_value("reference_no", "SI90" + ref);
        if (!frm.doc.mode_of_payment) {
            frm.set_value("mode_of_payment", "Nakazilo na TRR ");
            frm.set_value("bank_account", "Formis - Banka Intesa Sanpaolo");
        }
    }

});
