frappe.ui.form.on("Sales Invoice", "refresh", function(frm) {
let sales_orders = [];
$.each(frm.doc.items, function(idx, row) {
in_list(sales_orders, row.sales_order) ? "": sales_orders.push(row.sales_order)});
