var monthyear = moment().format("MM/YYYY");
var year = moment().format("YYYY");
var firstDay = moment().subtract(1,"months").startOf("month").format("DD.MM");
var lastDay = moment().subtract(1, "months").endOf("month").format("DD.MM.YYYY");
var lastmonth = moment().subtract(1, 'months').format('MM');

frappe.ui.form.on("Journal Entry", "voucher_type", function (frm) {
	if (frm.doc.voucher_type == "Plačilo prispevkov") {
		if (frm.doc.cheque_no === undefined){

		frm.set_value("cheque_no", "Plačilo prispevkov " + lastmonth +"/"+ year);
		frm.set_value("user_remark", "Prispevki PIZ, ZZZS, STV, ZAP od " + firstDay + " do " + lastDay);
		validated = true;
	}
	}
});
frappe.ui.form.on("Journal Entry", "voucher_type", function (frm) {
	if (frm.doc.voucher_type == "Provizija") {
		if (frm.doc.cheque_no === undefined){

		frm.set_value("cheque_no", "Vodenje računa " + + lastmonth +"/"+ year);
		frm.set_value("user_remark", "Stroški vodenja računa od " + firstDay + " do " + lastDay);
		validated = true;
	}
}
});
