frappe.ui.form.on("Sales Order", "validate", function(frm) {
sales_orders = []
$.each(frm.doc.items, function(idx, row) {
inList(sales_orders, row.prevdoc_docname) ? "": sales_orders.push(row.prevdoc_docname)
});
frm.set_value("ponudba", sales_orders.join(","))
});

frappe.ui.form.on('Sales Order', 'onload', function(frm){
 if (frm.doc.__islocal) { frm.set_value("delivery_date", frappe.datetime.add_days(frappe.datetime.nowdate(), 7));}
});
frappe.ui.form.on('Sales Order', 'onload', function(frm){
   if (frm.doc.__islocal) { frm.set_value("veljavnost", frappe.datetime.add_days(frappe.datetime.nowdate(), 7));}
});

frappe.ui.form.on("Sales Order", "refresh", function (frm, cdt, cdn) {
  cur_frm.set_query("item_code", "items", function () {
    return { 
      query: "erpnext.controllers.queries.item_query",
      searchfield: "item_code",
      //filters: {'is_sales_item': 1}
    }
  });
});

cur_frm.cscript.custom_validate = function(doc) {
    if (doc.naming_series == "DEL1-MB-.###")  {
        msgprint("Način plačila je bil spremenjen v GOTOVINA!");
cur_frm.set_value("mode_of_payment","Gotovina");
        validated = true;
    }
};

frappe.ui.form.on("Sales Invoice", "onload", function(frm) {
sales_orders = []
$.each(frm.doc.items, function(idx, row) {
inList(sales_orders, row.sales_order) ? "": sales_orders.push(row.sales_order)
});
frm.set_value("sales_order", sales_orders.join(", "))
});

frappe.ui.form.on("Sales Invoice", "onload", function(frm) {
delivery_notes = []
$.each(frm.doc.items, function(idx, row) {
inList(delivery_notes, row.delivery_note) ? "": delivery_notes.push(row.delivery_note)
});
frm.set_value("delivery_note", delivery_notes.join(", "))
});

