frappe.ui.form.on("Sales Invoice", "popravi", function (frm) {
	// use the __islocal value of doc, to check if the doc is saved or not
	if (frm.doc.popravi!= 1) {
		frm.set_df_property("placano", "read_only", 1);
	} else {
		frm.set_df_property("placano", "read_only", 0);
	}

});

