frappe.ui.form.on("Delivery Note", "validate", function(frm) {
sales_orders = []
$.each(frm.doc.items, function(idx, row) {
inList(sales_orders, row.against_sales_order) ? "": sales_orders.push(row.against_sales_order)
});
frm.set_value("so_no", sales_orders.join(","))
});

frappe.ui.form.on("Delivery Note", {
    refresh: function(frm) {
        // use the __islocal value of doc, to check if the doc is saved or not
        frm.set_df_property("datum_dostave", "read_only", frm.doc.status=="Closed");
    }
});
frappe.ui.form.on("Delivery Note", {
    refresh: function(frm) {
        // use the __islocal value of doc, to check if the doc is saved or not
        frm.set_df_property("nacin_dostave", "read_only", frm.doc.status=="Closed");
    }
});
frappe.ui.form.on("Delivery Note", {
    refresh: function(frm) {
        // use the __islocal value of doc, to check if the doc is saved or not
        frm.set_df_property("potrdilo", "read_only", frm.doc.status=="Closed");
    }
});

frappe.ui.form.on("Quotation", "stock", function(frm) {
frappe.model.with_doc("Kalkulacija stranke", frm.doc.stock, function() {
var s= frappe.model.get_doc("Kalkulacija stranke", frm.doc.stock);
frm.set_value("customer", s.customer);
frm.set_value("izdelek", s.izdelek);
frm.set_value("opis", s.opis);
frm.set_value("qty", s.qty);

frappe.model.with_doc("Kalkulacija stranke", frm.doc.stock, function(index){

d = frm.add_child("items");
f = frm.add_child("items");
if(s.sekunde==1){f.qty=(s.qty*s.razrez)/60;} else{f.qty=(s.qty*s.razrez);}
f.item_code="Razrez laserski";
d.item_code= s.doc_1;
d.item_name= s.item;
d.qty=s.area_total;
d.description=d.item_name;
f.description=s.izdelek
f.item_name=f.item_code
d.rate=s.price;
f.rate=s.work_price

cur_frm.refresh_field("items");
})
});
});

